'use strict';

var chars = [
  'aaaaceeiooouu',
  'àáâãçéêíóôõúü'
].map(function (char) {
  return char += char.toUpperCase();
});

module.exports = function (str) {
  if (typeof str === 'string') {
    return str.replace(/./g, function (char) {
      if (chars[1].includes(char)) {
        return chars[0][chars[1].search(char)];
      } else {
        return char;
      }
    });
  }
};